'use strict';

var app = function () {

    // Instance stores a reference to the Singleton
    var instance;

    function init() {

        var router = new window.router();
        var navbar = new window.navbarComponent(router);

        return {
            router: router
        };
    }

    return {
        getInstance: function getInstance() {

            if (!instance) {
                instance = init();
            }

            return instance;
        }
    };
}();