'use strict';

(function () {

    var app = window.app.getInstance();
    window.app.menuObserver.subscribe(toggleMenu);

    document.addEventListener('DOMContentLoaded', function () {
        document.querySelector('.main-logo').addEventListener('click', window.app.menuObserver.fire('menu click'));
    });

    function toggleMenu() {
        var icon = document.querySelector('.main-logo');
        var menu = document.querySelector('.content__menu');
        icon.addEventListener('click', function () {
            menu.classList.toggle('active');
        });
    }
})();