'use strict';

var navbarComponent = function () {

    function NavbarComponent(router) {
        var navbarNode = document.getElementById("nav-bar");
        navbarNode.addEventListener('click', function (event) {
            event.preventDefault();
            if (event.target.className === 'menu__link') {
                router.renderComponent(event.target.textContent.replace(/[^a-zA-Z]+/g, ""));
            }
        });

        document.addEventListener('DOMContentLoaded', function () {
            var icon = document.querySelector('.main-logo');
            var menu = document.querySelector('.content__menu');
            var menuActive = false;
            icon.addEventListener('click', function () {
                if (!menuActive) {
                    menu.className += " active";
                    menuActive = true;
                } else {
                    menu.className = menu.className.replace("active", '');
                    menuActive = false;
                }
            });
        });
    }

    return NavbarComponent;
}();